(window.webpackJsonp=window.webpackJsonp||[]).push([[38],{435:function(v,_,n){"use strict";n.r(_);var p=n(42),t=Object(p.a)({},(function(){var v=this,_=v.$createElement,n=v._self._c||_;return n("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[n("div",{staticClass:"info"},[v._v("\n「文はたてにも短くて」\n作者:みこう悠長\n投稿日時:20110215 2240\n")]),v._v(" "),n("div",{staticClass:"content"},[v._v("\n粗製濫造中。よって、相応の内容。\n"),n("h2",{attrs:{id:"それでもよければどうぞ。"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#それでもよければどうぞ。"}},[v._v("#")]),v._v(" それでもよければどうぞ。")]),v._v(" "),n("p",[v._v("「はたてさん、大丈夫ですか？」")]),v._v(" "),n("p",[v._v("真っ白い毛並みの白狼天狗が、私の手を取って立ち上がらせてくれた。")]),v._v(" "),n("p",[v._v("「あんた、文の」\n「犬走椛です。大丈夫ですか？あの人、手を抜いたりしないから」\n「手なんて抜いてもらわれて、堪るもんですか！」")]),v._v(" "),n("p",[v._v("ぱしっ、と取ってくれた手を払いのける。")]),v._v(" "),n("p",[v._v("「風切羽が切れてしまってましたよね。」")]),v._v(" "),n("p",[v._v("私の仕打ちを気に止めようともせず、私の心配をしている。人のいい奴。だが、なんだか、妙だ。こいつを見ていると、胸騒ぎがする。文の傍にいるやつだからと、反射的に警戒しているのだろうか。")]),v._v(" "),n("p",[v._v("「ふん。こんなもの、ほっとけばすぐにまた生えてくるわ。」\n「そうですか。私は見ての通り白狼天狗ですので」\n「あんたの爪とか牙と同じよ。」\n「よくわかりました」")]),v._v(" "),n("p",[v._v("掴みどころのない奴だ。文からきっとろくでもない教育を受けているに違いない。")]),v._v(" "),n("p",[v._v("「あんた、文の、何なのよ？」\n「何、というと？」")]),v._v(" "),n("p",[v._v("とぼけやがって。")]),v._v(" "),n("p",[v._v("「文の職位じゃ、直属の部下なんか持てるはずがないもの。それとも、あんたが白狼天狗らからって目を瞑ってもらっているの？」")]),v._v(" "),n("p",[v._v("文の職位は私よりも高いというわけではない。なのに何で文には部下がいて、私は未だにピンで取材をしないといけないわけ？\n　勿論鴉天狗の部下がいればそれに越したことはない。格下の白狼天狗ならば、文でも、つまり私でも部下につけることができるということか。")]),v._v(" "),n("p",[v._v("「私が文様についているのは、ちょっとした事情があるからです。本来、そう、本来は、ですね。本来は文様はただの新聞記者でしかなくて、一介の鴉天狗でしかなくて、つまりはたてさんと同じように、部下なんて抱えられる立場ではないんですが」\n「何よその含んだ言い方。文が文なら、あんたも相当性格がひねくれてるのね。」")]),v._v(" "),n("p",[v._v("まったく腹が立つ。")]),v._v(" "),n("p",[v._v("「ああ、いえちょっと、言いにくいことなので。そもそも本来私が、鴉天狗の下になんて、好き好んで付くはずが、ない。それがはたてさん、あなたであったとしてもです。」")]),v._v(" "),n("p",[v._v("犬走の目が鋭く尖る。なんて、禍々しい目。背筋が、凍った。たかが白狼天狗の癖に、なんて気迫を。いや、これは、殺気なの？傍にいるだけで肌が切り刻まれそう。\n　ちくちくと感じるその空気は、しかしすぐに収まった。")]),v._v(" "),n("p",[v._v("「大口を叩くわね、白狼天狗。脳筋の癖に、偉そうに。」\n「その口、あなたも御多分に洩れない鴉天狗ですね。あなたはどうやら、私と文様の仲が悪いと思っているようですが、それは訂正していただきましょう」\n「はっ、なあに？あいつと仲良しだとでも言うの？わらわせな」\n「私は、鴉天狗『全て』が、大っ嫌いだ。誤解なきよう。」\n「！？」")]),v._v(" "),n("p",[v._v("えっ、いま、あいつ、風を起こした？\n　ちがう。殺気と気迫だけで、私、空気が押し出されるように感じたとでも、言うの？")]),v._v(" "),n("p",[v._v("「あは、あはは、面白いわね。あんた」\n「犬走椛です」\n「犬走。あんたが何で文に付きまとっているのか、調べてやるわ。いい記事になりそうだし」")]),v._v(" "),n("p",[v._v("そうだ。\n　記者の嗅覚が訴えている。\n　ここにはスキャンダルが潜んでる。それを暴き出せば、文を、陥れられる。")]),v._v(" "),n("p",[v._v("「自信満々なのね。その『特別な理由』とやらが、ばれても構わない理由かしら？それともばれない自信がある？」\n「ノーコメントです。取材は構いませんけれど、一つだけ忠告しておきますね。」\n「なによ」")]),v._v(" "),n("p",[v._v("犬走はつかつかと歩み寄り、私の目の前に立つ。あれ、こいつ、こんなに大きかったかしら。違うな、背は私と同じくらいか少し大きい程度、って小さいってことなんだけれど、それなのに、がっちりしてる？ちがう、太いわけでもない。確かに肩や腕、脚の筋肉は強そうだが、しなやかで太いというほどではない。なんていうか、そう、重心が低いって言うか、安定しているんだ。こいつの足は、立っている場所から地下深くにまで杭を打ち込んであるみたいに、絶対にぶれない軸があるみたいな。\n　その、丸太がぶち当たってもバランスを崩さなさそうな体が、目の前に立つ。")]),v._v(" "),n("p",[v._v("「なによ、って」\n「あの人は、決して手を抜かない人です。今も思い知ったでしょう。たかが弾幕ごっこであなたの風切羽を切り落とした。文様と私の間を変に探って、陥れようとするのなら、どうなるか、覚悟はしておいたほうがいいですよ。」")]),v._v(" "),n("p",[v._v("犬走が私の顎を掴んで上を向かせる。何こいつ、小さいのはお互い様なのに、なんて考えていたら、ぞくり、背筋に何かが走った。犬走の言葉が耳から入り、上に向けられた目から、奴の視線が強制的に注がれる。そうされると、体が言うことを利かず、動悸が激しくなり、徐々に目の焦点がずれてうまく像を結ばなくなった。呼吸が落ち着かなくなり、酷く不快な感じが体を包み込む。\n　なんだ、これは。\n　私はパシッとその手を払って向き直る。離れてしまえば、不快感は改善した。だがそれでも依然としてぐつぐつとそれは、私の足元で煮えたぎり、私を飲み込もうとしている。")]),v._v(" "),n("p",[v._v("「はん、脅したって無駄よ」\n「勿論、文様に害をなすなら、私とて黙ってはいません」\n「武力行使が制限されている白狼天狗に、何ができるって言うのかしら？」\n「それは、どうでしょう」")]),v._v(" "),n("p",[v._v("くそ、なんだ、なんなんだ、この犬は……！\n　面と向かっているだけで、無性に焦りを掻きたてられる。不安感が広がって、苛立ちが募る。気分が悪い。さすが文の傍にいるだけある。しかも白狼天狗だなんて胸糞悪い。")]),v._v(" "),n("p",[v._v("「部下って言うより、小姓ね」\n「否定はしませんよ」")]),v._v(" "),n("p",[v._v("それだけ言い捨てて、犬走は森の中へと消えていった。")]),v._v(" "),n("p",[v._v("「ちょっと、私、飛べないんだけど！」")]),v._v(" "),n("p",[v._v("ゆってはみるが、犬走は戻ってこない。ここで戻ってこられたところで、奴に負ぶってもらうなんてごめんなのだけど。")]),v._v(" "),n("p",[v._v("「ちくしょうっ！」")]),v._v(" "),n("p",[v._v("私は下駄を脱いで笹で縛り、肩にかけて歩きなれない山道を歩く。\n　黙ってその辺に座って、羽が戻るのを待っていればいいものだったが、どうしても歩いて、体を動かしていないときが済まなかったからだ。\n　さっき、犬走に与えられた不快感は、まだ残っている。\n　私は、その正体を私は知っていて、でもそれを認めたくなかった。\n　なんで、なんだて、あんな白狼天狗に。")]),v._v(" "),n("p",[v._v("「一目惚れ、だって？ふざけないで……！」")]),v._v(" "),n("p",[v._v("◆")]),v._v(" "),n("p",[v._v("前回の新聞大会で結局後塵を拝してしまった私は、何とかして射命丸文と犬走椛のスキャンダルを仕留めたかった。写真だけでは記事にならない。ちゃんと取材をし、文面にも説得力を持たせて、文のいう『見た目のインパクトさえあればいい』新聞ではない、正統派の言論としての新聞で、文を駆逐する。それが、奴への最高に傑作な墓標になるはずだ。")]),v._v(" "),n("p",[v._v("「また、こっちもだめかあ」")]),v._v(" "),n("p",[v._v("だが取材は芳しくなかった。\n　何故か必ず天魔宮の大物辺りの疑惑にぶち当たり、それ故にあいつらの疑惑は益々持って強まりはするのだが、禿げた白鴉どもが尻尾を出すはずもなく、そこで文が何者なのか、手がかりとなる糸はぷっつりと途切れてしまう。\n　念写に頼ろうとするものの、そんな重要な映像が既出なはずもなく、私は文がそうするように自らの脚と翼で取材せざるを得なかった。")]),v._v(" "),n("p",[v._v("「文の下に白狼天狗が付いている。そのことは公然の事実で誰もが知っていることなのに、誰もそれを疑わないなんて。おかしい。おかしいはずなのに」")]),v._v(" "),n("p",[v._v("犬走の父親が、白狼天狗最後の大天狗長であったのは、私でなくとも誰でも知っている。その大天狗長失職して以来、白狼天狗の元々斜陽だった社会的地位は、転げるように落ち、今や被差別種族とさえ言っても差し支えがないほどになってしまっている。差別というよりは、隔離に近いだろうか。妖怪の山の混乱期に、武力を持って秩序を敷いた天狗の、主力は勇猛な白狼天狗たちだった。大きな争いが消え、狡賢い鴉天狗がその社会的地位を奪うと、その粗暴な力の主を、今度は厄介者扱いし始めた。\n　これが、言論としての新聞をしたためる私の取材結果だ。\n　このことは、一部の年長の天狗の間では公然の秘密であり、若い天狗の間では都市伝説として笑い飛ばされる性質を持っている。\n　私自身、白狼天狗に対して格下と見下す意識があるのだが、もしかすると、疑いも持たずにそうしてきた私自身が間違いだったのかもしれない。それはもはや自分の証明にも近い意味で、この取材を続けている。\n　そうしてその延長上に浮上してきたのが、射命丸文と、渦中の大天狗長の娘、犬走椛だった。")]),v._v(" "),n("p",[v._v("「ああ、もうっ。あと少しってところで線が切れる！伏せられた天狗の動乱期の闇を、あの二人の間に何があるのかを、どうしても暴きたい！」")]),v._v(" "),n("p",[v._v("かき集めた情報を紙に記して整理してみるものの、ビンゴゲームでダブルトリプルどころか５、６、７、列もリーチしているのに、一向にビンゴにならない、そんなもどかしさ。\n　政治とその裏側を調べるには、私程度の鴉天狗の力では、どうしようもないのか。")]),v._v(" "),n("p",[v._v("「文は、『あっち側』だとでも、いうの……？」")]),v._v(" "),n("p",[v._v("ただの新聞記者の文が、政界に食い込めるはずがない。犬走にしてもそうだった。もはや没落した家の血筋の娘などが。だからこそ、あの二人が一緒に行動していることについて、どうしても腑に落ちなかった。\n　犬走は鴉天狗全員を憎んでいる。\n　それは、父親が鴉天狗に消されたのだと思っているからだという。犬走自信そう公言しているし、私も取材で得られた情報から推してはかるに、そうだとは思う。だがその確たる証拠がないのも確かであり、何より、ああして文にくっついて回っている理由がわからない。\n　前回の新聞大会では、私と文は、奇しくも同じ事件を追って同じその記事同士で勝負することになった。そうして私は敗れたのだ。条件は同じはず。むしろ、念写が使える分、文の言う写真と見出しでインパクト、は、全くスポイルできる筈だった。なのに、なのに、文が提出した新聞の写真は、私が念写で撮影することもままならない厳重に管理された映像で、結局そのインパクトをスポイルできなかった私の敗北に終わった。\n　それが、腑に落ちない。あの区画は、普通の天狗が入れる場所でも、ましてや私が四六時中念写で張り付いていたのに撮影できなかった場所だ。何故、文が撮影できた。\n　文はそこに入ることができたのではないか？\n　何らかの方法で。\n　その写真が私の手元に届いたのは、文がその写真を撮影したと思われる十日後。つまり、新聞大会の祭りの後だった。")]),v._v(" "),n("p",[v._v("「文が『あっち側』の存在であるならば、確かに立ち入れた可能性は否定できない。けれど、そんな、そんなはずがない。私よりも羽が黒い下種だというのに、まさか、そんなはずはない。」")]),v._v(" "),n("p",[v._v("私の羽は、紫。黒からだいぶ色が薄くなった。このままいけば二千年後位には白くなって、私も高い地位につけるはず。だが、文は真っ黒だ。全く色を薄くしようとさえしない。そんな奴が、政界に通ずる権力を持っているはずが、なかった。")]),v._v(" "),n("p",[v._v("「腑に落ちない。あの二人のことが、何もかも腑に落ちない」")]),v._v(" "),n("p",[v._v("悔しかったのは、なにも取材が巧くいかないからばかりではない。でもその気持ちに忠実に動いてしまうのは、なお口惜しくて。どうしても二人の関係を暴いてやりたかった。\n　私の直感は、あの二人の間には、白狼天狗の隠蔽された歴史の闇にまつわる何か重大なことが横たわっているような気がした。いや、横たわっていないかもしれない。単純に、あの二人からは『これから』を、鴉天狗と白狼天狗の対立についての『これから』を感じさせられるような気がして。\n　それが、望ましい平和な未来というものが、犬走と文のよりによってあの二人の間から生まれるだなんて、信じたくなかった。\n　自らの手で、否定したい。否定の瞬間を、この手に収めたい。\n　二人を裂いて、犬走を。")]),v._v(" "),n("p",[v._v("「見ていなさい。絶対に」")]),v._v(" "),n("p",[v._v("◆")]),v._v(" "),n("p",[v._v("だめだ。\n　取材を続けていると、どうも犬走のことばかりが情報として手に入る。\n　文は当時大天狗長の下に付いてその補佐をしていたらしい。白狼天狗の大天狗長の下に、鴉天狗、しかも真っ黒な鴉天狗がつくなどとそれ自体が驚くべきことだ。殆ど家を出ることのなかった私は、写真として残されていなかったその歴史を初めて知った。\n　だが文のことはそれ以上手に入らない。上司の白狼天狗が引退（おそらく暗殺されたのだろうが）してからは、写真どころか文字媒体や人の記憶にさえ、歴史として登場しない。取材をして出会う情報は、そこからはただただ、犬走のことだった。\n　鴉天狗と白狼天狗の種族抗争において消されたであろう白狼天狗の大天狗長。その娘の犬走椛。天狗社会の要職を次々に失職する白狼天狗達の中、ひたすらに雌伏し続け、今に至る。史実としては、白狼天狗内の内部分裂によって、白狼天狗の手で失脚に追い込まれたとされているその大天狗長は、きっと鴉天狗の手によって殺されたのであろう。犬走は、その無念を抱いたまま、今、文の部下となっている。\n　白狼天狗の部下だったという異色の経歴を持つ文だが、それゆえにだろう、未だに私と同じ一介の新聞記者で、もっと言うなら、全く色が薄くなっていない。真っ黒なままだ。\n　かつて父親の部下だった文の、今は部下に甘んじている犬走。白狼天狗の中で名門であった犬走家の嫡子でありながら、下っ端で真っ黒な鴉天狗の配下として、今何を考えているのだろう。\n　そうだと考えれば、犬走が文だけを鴉天狗の中で特別視するのは、合点がいかなくもない。\n　だが、どうだろうか。\n　元々職務上最も近くにいることができたのは、文だ。もしかして、犬走の父親を殺したのは。")]),v._v(" "),n("p",[v._v("「だめだめ。短絡的過ぎる。ゴシップとしては面白いけれど、報道としては使い物にならない。少なくとも証拠を揃えないことには。」")]),v._v(" "),n("p",[v._v("犬走のこと、そればかり。\n　私の前に立ち、私を見下した、あの目が忘れられない。\n　私よりも大きいとはいえ、決して大柄とはいえない体に感じられる、底知れない力強さ。しなやかな四肢、少しハスキーな声。\n　取材中に犬走椛の文字が現れるたびに、どきりと胸が異音を発し、それを必死に落ち着けてから情報収集に戻る。\n　恥ずかしいほどに、私は、犬走に惹かれていた。\n　一目見ただけなのに、体の心から抉り取られるみたいな衝撃と、あれから何回も顔を合わせているわけでもないのに、頭の中で毎日顔を突き合わせているような錯覚。混乱。\n　恋心。\n　あの腕に、抱かれたい。あの鋭い瞳で私の全てを見て、体中を視線で愛撫して欲しい。低い声で耳元に私の名前をそっと置いて欲しい。")]),v._v(" "),n("p",[v._v("「なんで、なんでなのよ」")]),v._v(" "),n("p",[v._v("なんで犬走は犬走なのだろう。\n　なんで犬走は白狼天狗なのだろう。\n　なんで犬走は文の部下なのだろう。\n　何で私は、犬走に惚れてしまったのだろう。\n　百歩譲って文の方が、まだ体裁も、いや、悪いか。")]),v._v(" "),n("p",[v._v("私は頭をかきむしってから、ふう、と息をついて意図的に思考を切り替える。\n　だめだ、こんなんでは。\n　携帯電話を取り出して、見落としている写真がないか確認する。\n　何も新しい情報だけが情報ではない。古くても見落としがあれば、それはもしかしたら重要な手がかりかもしれない。もう夜も遅い以上、家でできることは限られていた。\n　『白狼天狗　種族抗争』『射命丸　犬走』『暗殺　大天狗長』『大天狗長会議　出席　天狗　種族』『山伏天狗　抗争　戦力』『鼻高天狗　勢力　傘下』『スク　本名』『射命丸　色　変化』『白狼天狗　鴉天狗　強い』\n　次々に関係しそうなワードを入力していくが、全く見たことのある写真ばかり。")]),v._v(" "),n("p",[v._v("「ふう、やっぱり、だめか。悔しいけれど、文の言うとおりなのかしら」")]),v._v(" "),n("p",[v._v("自分の足で写真をかき集め、とにかく鮮度を重要視する文。記事の稚拙さには目を覆いたくなるけれど、昔からあいつを知っている私は、あいつがまともな記事を書けないわけではない事くらい、知っていた。あの記事は、文なりのメッセージなのだ。\n　私はそんな皮肉ったやり方はできない。だからきちんと記事を書く。だが、仮に文ほどに鮮度の高い映像ネタを入手することができても、私の記事の作り方では、その活きを生かすことができないのだ。\n　文と私は、表裏一体。拙速か巧遅か。おそらく勝敗は決している。\n　記事の内容が正確で真実であろうとも、それが遅く地味であるのなら、むしろ速くインパクトのある方が内容如何にかかわらず、評価されるのだ。今は、そういう時代なのだ。")]),v._v(" "),n("p",[v._v("「それでも、家の中にこもっていたんじゃだめだって、教えてくれたのは文なのよね。悔しい、くそっ」")]),v._v(" "),n("p",[v._v("書きかけの記事。それを締めくくってから自分の目の前に広げ、改めて見てみる。ここまでは、見栄えはよい。文が言うようにインパクトも付けてみたし、自分で撮影した写真も使った。悔しいけれど、これも、あいつを追い落とすためだ。そしてそれを上回るための、文々。新聞にはない正確に取材し客観性を重視した上で、最後にこの新聞としての主張を小さく述べて締めくくる記事。\n　悪くないはずだ。こうして地道に品質を上げ、そのノウハウを次の新聞大会で発揮する。\n　そしてそのときは、必ず、あの二人を暴いてみせる。")]),v._v(" "),n("p",[v._v("「犬走……」")]),v._v(" "),n("p",[v._v("ぱしゃり、新聞を机の上に叩き付けて、頭の中で暴れ回るイメージを沈めようと葛藤する。\n　白いもやがどうしてもあいつの形になり、それを振り払うように映像を打ち消すとそれは再びもやに戻り、やがてまたあいつの形になる。\n　色だけは付けまいと頭を掻きむしってもそれは徐々に色を得てゆく。肌には血色が宿って命を灯し、真白い毛並みにも薄い青、紫、灰の機微が流れて風に凪がれる。唇は、赤く、赤く、艶めいて揺れる。刺さるナイフのように鋭い瞳の奥が、それを呑み込むほどに、更に赤い。\n　彼女は裸だった。女の割りに形のいい肩、少し薄い胸、引き締まり筋張った腕・脚・背中。とても女の色気があるとは思えないがっちりした腰つき。決して女らしいとは思えない体躯に。")]),v._v(" "),n("p",[v._v("「だめ、だってば」")]),v._v(" "),n("p",[v._v("想像してはいけないと幾ら自戒しても、想いが止まることはなかった。")]),v._v(" "),n("p",[v._v("「『犬走椛』」")]),v._v(" "),n("p",[v._v("携帯電話にワードを入力し画像を表示する。脳内で描いていた彼女のイメージとは少し違う、それでも十分すぎる程に、私にとって十分すぎるほどにエロティックな肉体が表示される。\n　どの部分が露出しているわけではない。山を駆け、木を跳ね、青竜刀を薙いで、術式を起動する、それだけの、画像。だというのに、私はそれをみながら、いよいよ布団に潜り込んで股の間に指を這わせた。")]),v._v(" "),n("p",[v._v("「なんで、こんな、やつのこと……んっ、ふ」")]),v._v(" "),n("p",[v._v("携帯電話のボタンを押して、めくるめく表示される犬走の姿。大地を蹴る脚、刀を振るう腕、全てを見抜く瞳。\n　それを見ながら少し触れるだけで、私の股間は熱く潤んでどんどんと蕩けてゆく。溢れ始めた愛液は、指に絡み付いて淫裂から零れ、陰唇をぬめりで湿らせてゆく。")]),v._v(" "),n("p",[v._v("「す、ご、もう、こんなに、なんて、私」")]),v._v(" "),n("p",[v._v("左手は携帯のボタンを押し続け、より力強く、より拡大された画像を選んでは表示し、その画像を元に脳内に淫らな犬走の像を描いてしまう。")]),v._v(" "),n("p",[v._v("「んっ、く、あ、犬走、ぃっ」")]),v._v(" "),n("p",[v._v("譫言のようにあいつの名前を口に漏らすと、それがトリガとなって快楽が急激に膨らみ始めた。\n　横向きになって体を丸めて股間をまさぐっていたのに、いつの間にか仰向けに体を広げて、股を開いて雌溝を擦り上げていた。")]),v._v(" "),n("p",[v._v("「あっ、ん、ふぅっ！濡れすぎ、私、あんなやつオカズにして、しかも濡れすぎぃっ！こんな、こんなの、毎晩してるなんて、言えないっ！絶対、知られたくないっ！ん、んふぅうううっ！でも、でも気持ちいい、犬走なんかのオカズで私、感じまくってるぅっ！」")]),v._v(" "),n("p",[v._v("ぐちゅぐちゅ音が聞こえるほどに指で掻き回す。出し入れはぬめりを伴ってスムーズ。人差し指中指薬指をまとめて突っ込んでそれを別々に動かすと、粘液は空気を含んでぐちゅぐちゅと下品な音を響かせた。")]),v._v(" "),n("p",[v._v("「あ、はっ、はーっ、はあっ！いい、いいっ！犬走の指、ぎもちぃっ！おく、もっと、おくぅっ！」")]),v._v(" "),n("p",[v._v("腰が持ち上がって、妄想の中の犬走は私のヴァギナを容赦なくいいじり倒す。指を入れ、中で曲げてへその裏側を触ろうとするイメージ。奥へ奥へと押し込んで肉壁をさらに強く擦り上げると、びりびりした快感が放電して発熱する。\n　締め上げる淫肉壷がねだるままに指ペニスで慰めると、しゅくしゅくと締まりが強くなって、摩擦もまた強くなる。\n　犬走の写真は、もうほとんどが見たことのあるもの。たまに見たことのない写真が新しく紛れ込むが、新しかろうが古かろうが、私のオカズには十分だった。\n　犬走の照れたようなはにかみ顔をおさめた、恐らく文が撮ったのであろう写真だけは、いつもフィニッシュに使っている。その目を、その顔を、私に向けて欲しい。オナニーをしているときだけは、その照れた顔は私のもの。")]),v._v(" "),n("p",[v._v("「犬走っ、っぁあっ！ん、ふうっっん！」")]),v._v(" "),n("p",[v._v("と、見慣れないサムネイルが新しく紛れ込んでいた。\n　それを選択して拡大表示したところで、私は息を呑む。")]),v._v(" "),n("p",[v._v("「なっ……！」")]),v._v(" "),n("p",[v._v("表示されていたのは、しわくちゃになった臥所、投げ飛ばされた布団、その真ん中で躍る二つの裸体。片方は、犬走。もう片方は、文。それは、犬走と文のハメ撮り写真だった。")]),v._v(" "),n("p",[v._v("「う、そ」")]),v._v(" "),n("p",[v._v("二人が何らかの形で親しいだろうことは予感していた。だが、正直、体を重ねるほどだなんて、考えてなかった。\n　声が出ない。写真の中の二人は、もちろん二人きりのセックスとはそうであるように、二人だけの世界に没入している。")]),v._v(" "),n("p",[v._v("「犬走……」")]),v._v(" "),n("p",[v._v("文の上にのって、たくましい肉棒を文の淫裂に挿入していた。\n　その秘裂はしとどに濡れ、犬走のペニスを受け入れるために自分でか、もしくは犬走がそれを執拗にほぐしただろうことがわかる。\n　私が思っていたよりもずっと太くて長い犬走のペニス。真っ赤に充血した淫牡肉が、とろけた淫売の牝壺に突き刺さり、その本気汁を誘い出しているのがわかる。\n　ハメ撮りの雰囲気を殺さない、明るすぎない照明の下でも、二人の太腿辺りをぬらりとぬめり光らせる淫液が、その濃厚なセックスを物語っていた。")]),v._v(" "),n("p",[v._v("「はっ、ん、だめ、なのに、こんなぁっ」")]),v._v(" "),n("p",[v._v("何枚も収集された、犬走と文の性交写真。\n　体位を変え、表情を変え、中には犬走の想像を絶する量の吐精を頭の上から上半身全体で受け止めて、真っ白に染まりながら自らの秘所をまさぐって滴る精液をすする文の姿。\n　そんな今までとは比べものにならない刺激的な写真を前にして、私の手淫は熱を帯びる一方だった。")]),v._v(" "),n("p",[v._v("「犬、走、そんな、の、すごいよぉっ……あや、私と代わって、かわってよぉ、犬走のそのぶっといペニス、私が欲しいのよぉっ！」")]),v._v(" "),n("p",[v._v("女陰をまさぐる指の勢いは増し、部屋を満たす雌臭はどんどん濃く立ちこめていった。くちゅくちゅと汚らしい水音も鳴り響いて、腰がその刺激を求めてがくがくと揺れる。\n　次に開いたのは、文に上にのられて、切ない喘ぎ顔になって射精を我慢させられている可愛い犬走の姿。")]),v._v(" "),n("p",[v._v("「犬走ぃ、っ！いぬばしりっ！それ、文が出させてくれないなら、それぇっ！私に、私に頂戴よぉっ！私だったら、私だったら幾ら出してもイイからっ！我慢なんてさせないからぁっ！あんたが好きなように、私を犯していいからっ！好きなだけ中出しも、口に出してくれても、お、お尻の穴でも、何処でも使わせたげるからぁあっ！！」")]),v._v(" "),n("p",[v._v("誰も聞いていない、私が私を高ぶらせるためだけの淫らな言葉を大声で叫びながら、私のオナニーは絶頂に向けて突き進んでゆく。\n　こうして毎晩犬走のコトを考えながらオナニーしているせいで、殆ど顔を合わせてもいないのに私の恋心はどんどん大きく深くなっていく一方で。\n　そうしてそれがまた、毎晩の手淫を我慢できないでいる原因。この悪循環は、留まることを知らない。まるで私がイイ新聞記事を書けないで々ところをくるくると回り続けているのと同じように、いや、もっと惨めでもっと甘ったるい悪循環。\n　犬走と文のエロ画像次から次に表示しながら、それを食い入るように見つつ私の手淫はますます激しく淫らに花を咲かせてゆく。淫液は増し、充血した女性器も感度を増長させて、クリトリスも、くん、と天を突いて次の刺激を待ちかまえている。それを容赦なく押しつぶし、奥をかき混ぜ、淫液を股間といわず、胸や顔に塗りつけて倒錯した快楽の中に埋没していく。")]),v._v(" "),n("p",[v._v("「っは、すごい、この写真、すごいっ……！ハメ撮りして楽しんでるなんて、あの二人、ずるい、ずるいっつ！文、ちくしょう、ちくしょうっ！いつも、あんたはいつもいつも私の先回りをして、悔しいっ！くやしいっ！でも、でもこの写真はっ、エロ過ぎて感じちゃう……！くそぉっ！あやぁ、いぬばしりぃっ！」")]),v._v(" "),n("p",[v._v("もう、もう、イきそう……悔しい、文が、犬走としているところをオカズにして、だめ、もうっ！\n　次の写真で、イく。膣が指を食いちぎりそうに締り、下腹部が熱く火照っている。愛液がいっそう量を増して、白く濁ったそれは私が本気であいつの、犬走の体を欲していることを示していた。\n　右手で膣を引っ掻き回しながら、左手で、次の写真をめくる。きっと、文の中に犬走が挿入している写真が。\n　私はもう絶頂寸前で痙攣を始めているあそこを我慢しながら、次の写真をめくった。\n　そこに映っていたのは。")]),v._v(" "),n("p",[v._v("「ん、ひ、あ、み、見て……るっ？私の、こと、ふたりともぉおっ！？お、おひ、イく、私、見てるつもりだったのに、みられ、見られて、イくぅっ！窃視オナニーだったのに、最後、視姦アクメなん、へ、ひ、みへ、みへぇっ！いぬばしり、わたし、あんたたちのせっくす、覗き見しながら、お、おぉおおっん！盗撮オナニーで、、なのに、にゃのに、ぎゃくに、みらりぇへ、イ、イっひゃうのおおぉおおおっ！！」")]),v._v(" "),n("p",[v._v("映し出されていたのは、まぐわり、快感を訴える表情のまま、しかし視線だけをこちらに視線を投げる、文と犬走の映像。\n　淫蕩に浸る文の表情。上気した肌に舌で唇を嘗めるようにして妖しく笑っている。椛のちんぽ、気持ちいいっ！音の聞こえない写真であるにも拘わらず、文の嬌声が響いてきそう。\n　快楽に喘ぐ犬走の容。汗ばんだ肌に口を半開きにして舌を垂らし切なそうに息を上げている。文様の膣内、熱くて溶けそうですっ！声のない写真であるにも拘わらず、犬走の咽び声が聞こえてきそう。")]),v._v(" "),n("p",[v._v("その二人の視線は、まっすぐにこちらを見ていた。\n　わかる。この写り方は、写真に写るためではない。\n　ファインダーの逆向こうにいる誰かへの、明らかな視線。\n　二人は、こちらを、いや、私を、見ていた。")]),v._v(" "),n("p",[v._v("文は犬走のペニスに貫かれた快感を、犬走は文に挿入する快感を私に見せ付けるみたいに、快楽に蕩けた表情を私に見せつけて、でもその視線は私を嘲り、その内心は笑っている。")]),v._v(" "),n("p",[v._v("犬走の、テレグノシス……！")]),v._v(" "),n("p",[v._v("私は、見抜かれていたのだ、きっと私がこんな風にオナニーしていると知って、私に見せ付けるみたいにセックスを始めて、私はまんまと罠にはまって、そのことを犬走から聞いた文は犬走と二人で、私を、私を！\n　私が念写した写真の片隅には、何かの写真が散らばっている。それはおそらく文のカメラで撮ったであろう二人のハメ撮り写真、さっきまで私が見てオカズにしていた写真そのものだったのだろう。\n　それこそが、私を誘い入れるための罠だったのだ。")]),v._v(" "),n("p",[v._v("「イ、くぅっ！♪イっちゃうぅっ！！♥や、やめひぇ、みないれぇえっ♪♥これ以上、これいじょぉ見られアクメ、いやぁあっ！恥ずかしいっ、私、ハメ撮り写真見ながら罠にハメられちゃって、それでかんじまくひぇりゅぉおぉおおおんっ！っほ、はひぃいっ！♥」")]),v._v(" "),n("p",[v._v("私はそこまで助走をつけて上り詰めようとしていた快感を押し留める事ができずに、文と犬走に『見らている写真を見ながら』、果てた。\n　しかも私はそのままその写真から、目が離せない。二人の視線を浴びながら、私のそこは自分の指を犬走のペニスだと信じて精液を求めてきつく締まった。愛液の雫を飛ばしながら、私はその『写真に見られながら』何度も何度も上り詰める。")]),v._v(" "),n("p",[v._v("「っひ、お、ぁあ、ああっ！い、いつまで、いつまでみてるのよぉっ！いつまで、私をイかせるつもりよぉおおっ！♥アクメ、見られアクメとまら、なっ……ひ、ひぁ……♪♥んっひ、ひいぃっ！♥みない、で、みにゃいれえっ！私、目ぇはなせないっ！二人から念写の意趣返しれ視姦しかえされひぇ、逃げられないっ！体が、体が見られたがってりゅうっっっ！♥イきまくりのだらしない顔、みへ♪みひぇええっ！♥はーっ、はーっ！興奮っ、みらりぇてこーふゅんしひぇゆうううぅぅっ♪ハメシーン覗き見するつもりが、アヘ顔晒して見られてイき終われなくなってるのおおおっっ！」")]),v._v(" "),n("p",[v._v("写真から、目を離せず、離せない限り、オーガズムは続く。視線が体中の快楽神経をレーザーみたいにじりじり焼き潰していく。いくらイっても、もう全身に無数に張り巡らされたアクメ導線は焼き潰しきれず、永遠に続くとも知れぬ視姦快楽が終わらない。\n　ぶちぶちと筋を千切るくらいの思いで写真から目を離す。それでも私の体を射抜く視線の串刺し快感はとまらない。ぎゅっと目を閉じ、写真の映像を視界から追い出すが、もう快感が一人歩きしていた。")]),v._v(" "),n("p",[v._v("「も、もう、みなひれ……もう、イけにゃひ、もうイき耐えられないっ！♥頭の中ぷつぷつゆっへ、のーみそ、やききれひぇ、ゆるひ、もう、ゆるひへぇぇえええっ！♥っは♥っはぉおおおおんっっ！♥見られながら、まんこイきまくってとまんないひぃっ！♥自分指ちんぽぎゅうぎゅう締まって、マン汁飛ばしまくってゆぉおおおっ！ぎもぢいぃ、見られるのきもちぃいっ♪でももういやにゃの、もうイきたくなひのおおおおおぉぉおおっ！♥」")]),v._v(" "),n("p",[v._v("無理矢理視線を引きちぎった携帯電話を、しかし自分の体の隅々にまで這わせてしまう。\n　顔を離れて胸、へそ、太股に尻。\n　快感にとかされまくって、醜態を止めることが出来ない。自ら進んで視姦を受け、そのたびに感じてしまう、法悦を極めてしまう私。\n　やがて指を入れて、その突起を擦ってオナニーしている淫裂の様を、二人の視線の前に晒す。")]),v._v(" "),n("p",[v._v("「っひ、ふぎぃいいいいっ♪♥んほぉっ♥はーっ♥♪はぁぁああっ！♥♪きもちぃい♥♪見られ手マンきもぢぃいのぉおおっ！♥犬走、みて、みてぇっ！わたし、あんたのこと考えながら毎日こんなふぅにぃ♥あんたの写真見て、大声でアヘりながらオナニーしまくってたのぉっ！♥ひみつ♪絶対の秘密だったのにいぃぃ！♪♥犬走だけじゃなくって、文にまで、こんな、こんな、指四本もぶち込んでぐちゅぐちゅかきまぜながら、親指でクリちゃんひねり潰しまくってるトコロ、みられちゃって、もう、最悪♥♪最悪なのぉっん！♥♪最低のシチュエーションで、私、オナニー最高にもえてゆぅっ♪イき終わらないっ♥どんどんおっきくなっひぇ、アクメ波つよしゅぎちゃって♥もう、もうっ！あああああああぁぁっ♪♥もう、いっそ、こうして、こうしちゃうのおおおおぉっぉおっ！♥♪」")]),v._v(" "),n("p",[v._v("イきまくりのまんこにぎゅうぎゅう食いちぎられそうになっている四本の指を抜くと、解けた肉ひだがでろりとはみでた。でっぷり充血したラヴィアが抜き去られた獲物へ名残惜しく粘液の糸を張り付けている。\n　乱暴に掻き回しまくった蜜壷はぱっくりと口を開けたままその暗い空洞を閉じることなく、入り口から最奥までの全部の肉壁がぐねぐね蠢き、だらりと粘液を垂らして、次の挿入を待ちわびている。\n　私はその節操のない雌穴に向けて、手に持っていた携帯電話をぶち込む。")]),v._v(" "),n("p",[v._v("「っほ、ほごほぉおぉおおおおっっ！♥ごり♪ごりっ！♪二人の視線がごりごりクるぅっ！♥ぼっこりあいたマン穴、直接見られて、奥まで、おくまでみらりぇてぇぇっ！♥はあっ、んふぅおっ、おぉおっっほ、っ、っっっっっ～～！♪♥っは、奥のお肉がぁ、犬走に見られてよろこんじゃってりゅぅっ！♥みひぇ、みへぇぇっ！♥盗撮オナニストはたての、手淫慣れした広がりまんこ、もっろみひぇええええええぇぇええぇんんっっっ！♥あや、あんたはみなくていいの、いいのぉっ！♪みなくていいのぉっ！♥いいっ、いひぃいいっ！♥♪ぎもぢぃいいいいぃいいっっ！♪もう、もうらめ、まっちろ、頭の中も、世界も全部まっしろぉっ！きもちいので、きもちいのでぬりつぶされひゃったぁっ！♥きひぃいっ、おかして、視線だけじゃなくて、もっと、私、おかしひぇえぇえっ！♪♪♥」")]),v._v(" "),n("p",[v._v("ブリッジしながら、両手でまんこに携帯をぶち込んで、それを出し入れする。携帯の液晶に映し出された二人の表情に、肉ビラがからみついて肉色のナメクジみたいにずるずる引き延びてはマン汁を塗ったくってゆく。\n　視界が明滅して、ちかちかと意識が断続する私は、最後の最後に自分に止めを刺すつもりで、下半分が淫裂から顔を出している携帯電話を素早く操作し、今まで刺さっていた方とは逆の文字盤の方を雌密壺へぶち込む。\n　ヴヴヴヴヴヴヴ……と、低い音を響かせて、携帯電話のバイブ機能が起動して、絶頂で締まっているにも拘わらずずぼずぼと緩くそれを加える濡れまんこを激しく掻き回した。その中から半分だけ顔を覗かせた液晶画面には犬走と文の顔が写って、その向こう側から私の絶頂を繰り返すヴァギナをじっと見つめている。")]),v._v(" "),n("p",[v._v("「ああああァああああぁああアああああっ！！♪♥♥♪ひ、ひいぃいいいっ！♪♥♥すご♪っ♥すっ♪ごおぉおほひいいいいいいっっっっっっ！♪♥おまんこ、おまんこの奥でがりがりが、ぶるぶるして♥子宮♪ちょくせつゆさぶられちゃってりゅううううううぅぅぅうっ！♥イっ、ぉほおおおっ♪！！♥ぎひいいいぃぃいいいィえィイいいいぃいい！♥♥はあっ、はっはあああっぁん！♥ふるっ、ふるえてゆっ！♥犬走の視線がっ！♥♥はげしいっぃ、はげしいぃいいいいっ！♥お腹の中♪マン汁攪拌されてあわだっちゃってるのぉおおおおおっ！♥みへ♪みひぇえええっ！！♥二人のバイブでまんこぶちゅぶちゅなって♪マン肉はみ出てびらびらしまくってるぅっ！♥勃起クリ、みられへっ！♥バイブでぶるぶるゆれてズル剥けになった勃起クリちゃん♥そんなにみないでぇっ！♥みない、で♪み♪みて♪みてぇっ！♥♪携帯バイブでまんこ泡立てながら、犬走と文に視姦されてマン汁噴きまくってる色キチガイの私の、絶頂してるとこ、イきまくって、大股開いて持ち上げた腰がっくんがっくん振ってるとこ♥み、みへぇあえええええええエえエぇェっ！！！♥♪♥」")]),v._v(" "),n("p",[v._v("体の奥底から、大きな闇が閃光の波をまき散らしながら溢れだしてきた。加速度的に巨大化するその姿は私の中から一気に全身を呑み込んで、体中の快楽神経に金属ブラシを立てて引っかき回していく。手、足、お腹、背筋、胸、首筋、頭。全身のビニールチューブに薄紫の液体が通って、それが全身に駆けめぐる。血液の代わりに熱くて気持ちのいいそれが注がれると、体中から力が抜けて、弛緩するかと思えば強ばったまま動かない。股間から与えられる感覚を全身が本来の快感の難波言うにも増幅させてその愉悦を脳みそへ強制挿入してきた。")]),v._v(" "),n("p",[v._v("「～～～っ！♪♥あっ、く……ぅ、ひっーーーーーー！！っ！♥」")]),v._v(" "),n("p",[v._v("もはや声を出すことも出来ない。目玉がこぼれ落ちそうなほどに見開いて涙をぼろぼろながし、歯を食いしばって涎をだらだら流し、全身を逆さまの弓みたいにしならせたまま、股間から粘りけのある液体をびゅっ、びゅっ、と何度も吹き出して、やがてそこに突き立っていた無機質な視線とペニスが、にゅるりと抜けておちる。\n　ごとり、という音の他にばしゃ、という水音が響いたような気がしたがそれももはやわからない。\n　犬走と文の幻に犯されながら、降りることのできないオーガズムのてっぺんで、私は意識を手放すしかなかった。")]),v._v(" "),n("p",[v._v("◆")]),v._v(" "),n("p",[v._v("「犬走、私、あなたのことが好きなの」")]),v._v(" "),n("p",[v._v("あんな姿を見られていたのだ。頭のてっぺんから脚のつま先まで、肥溜めに埋まるみたいに罠に沈んだ私は、その気持ちをもう隠し通すつもりなどなかった。私は犬走を呼び出して、勝利を確信して明かすか、それとも永遠に秘匿しておくつもりだったジョーカーを、たった数ヶ月で、しかも敗北の末に、開示することになった。\n　ジョーカーは、ジョーカーだったのだ。\n　私の突然の告白に、さしもの犬走も、まさか本気でぶつかってくるとは思っていなかったらしく、驚きを隠せないでいる。\n　別に色のいい答えを期待していたわけではない。私自信白狼天狗を見下しているそのそのことへの疑念と証明のために白狼天狗凋落の真相を追いかけるのと同じように、単純に、私の中に決着が欲しかっただけ。どちらであっても構わない。決着を、勝敗をつけたい。それだけ。それだけだ。\n　言葉にして伝えれば、少しは揺らぐかなんて、考えていない。\n　嘘。\n　少しは期待した。")]),v._v(" "),n("p",[v._v("「残念ですけど」\n「わかってる。文が、好きなんでしょう？」\n「ええ、それに、鴉天狗と付き合うなんて、ありえません。」\n「は？だって、文も」\n「あの人は」")]),v._v(" "),n("p",[v._v("いったん言葉を止めて、何かを考えるようにしてから。\n　そっと私の耳元に言葉を置いて、背を向けた。")]),v._v(" "),n("p",[v._v("「ど、どういう意味？文が、あなたのお父さんの部下だったから？」\n「それは、はたてさんの今後の取材次第ってところです。ゆっくりでもいいです。真実を、調べ上げて下さい。但し、静かにお願いします。」")]),v._v(" "),n("p",[v._v("犬走は身軽な様子で歩きなれた山の中へその姿を消した。\n　私はその背を、その姿が山の中に消えるのを、呆然と見つめる。\n　犬走が置いていった言葉。")]),v._v(" "),n("p",[v._v("――あの人は、白狼天狗の魂を受け継いでいますから――")]),v._v(" "),n("p",[v._v("魂。\n　上司と部下なんて関係では、到底現れようもない言葉。\n　その意味が、私には全くわからない。\n　文に負け、犬走にも負け、私は悔しさに唇を噛んで足元の土を踏み締める。\n　それでも、これが、今の私の力の限界なんだ。")]),v._v(" "),n("p",[v._v("「私にできるのは、報道を続けること。追い続けること。それだけ。文とは違うやり方だけれど、それで、真実を写し出してみせる。」")]),v._v(" "),n("p",[v._v("いつか、必ず、犬走に、文に、何よりも今の自分に、克つ。\n　それだけを胸に、自分に言い聞かせた。\n")])]),n("p"),v._v(" "),n("div",{staticClass:"afterword"},[v._v("\nあとがき----------------------------\n伏線についてはいずれ。\n天狗周りは能力がビジュアルに寄ってるので視覚情報を使って遊びたくなります。\n")]),v._v(" "),n("div",{staticClass:"comments"},[v._v("\n【1.名前が無い程度の能力[2011/02/17 02:39:38]】\n抜いた\n【2.名前が無い程度の能力[2011/02/24 20:52:44]】\nこれはエロい\n")])])}),[],!1,null,null,null);_.default=t.exports}}]);